// components/performance/image-optimizer.tsx
'use client'

import { useState } from 'react'
import Image from 'next/image'

interface OptimizedImageProps {
  src: string
  alt: string
  width: number
  height: number
  className?: string
  priority?: boolean
}

export function OptimizedImage({ 
  src, 
  alt, 
  width, 
  height, 
  className,
  priority = false 
}: OptimizedImageProps) {
  const [isLoading, setIsLoading] = useState(true)
  const [hasError, setHasError] = useState(false)

  return (
    <div className={`relative ${className}`}>
      {isLoading && (
        <div className="absolute inset-0 bg-slate-200 animate-pulse rounded" />
      )}
      
      <Image
        src={hasError ? '/images/placeholder.jpg' : src}
        alt={alt}
        width={width}
        height={height}
        className={`
          transition-opacity duration-300
          ${isLoading ? 'opacity-0' : 'opacity-100'}
          ${hasError ? 'grayscale' : ''}
        `}
        priority={priority}
        placeholder="blur"
        blurDataURL="data:image/jpeg;base64,"
        onLoad={() => setIsLoading(false)}
        onError={() => {
          setIsLoading(false)
          setHasError(true)
        }}
      />
    </div>
  )
}

// hooks/performance/use-performance-metrics.ts
import { useEffect } from 'react'

export function usePerformanceMetrics() {
  useEffect(() => {
    const observer = new PerformanceObserver((list) => {
      for (const entry of list.getEntries()) {
        console.log(`${entry.name}: ${entry.duration}ms`)
        
        // Envoyer les métriques à votre service d'analytics
        if (entry.name === 'Next.js-hydration') {
          // Log du temps d'hydratation
        }
      }
    })

    observer.observe({ entryTypes: ['navigation', 'paint', 'measure'] })

    return () => observer.disconnect()
  }, [])
}
